sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var encodedURL = inputs.encoded_url;

  var url_parts = encodedURL.split("?")[1];
  var params = url_parts.split("&");
  var target_url = "Error:  couldn't find target URL.";

  for(var n=0; n<params.length; n++) {
       var namval = params[n].split("=");
        if(namval[0]=="url") 
          target_url = namval[1];
    }

    outputs.decode_url = decodeURIComponent(target_url);

})(inputs, outputs);

